INSERT INTO `wenhuas` VALUES (1,'云南奇彩鲜花食品有限公司','<p class=\"cjk\" align=\"center\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-size:18px;\"><b>企业简介</b></span></span>\r\n</p>\r\n<p class=\"cjk\" align=\"center\" style=\"text-align:left;\">\r\n	<span><span><b>\r\n	<p class=\"cjk\" style=\"text-indent:0.74cm;\">\r\n		<span style=\"font-size:16px;\"><span style=\"font-size:16px;\">玫瑰代表着爱；有玫瑰的地方就有爱！我们种植</span></span><span style=\"font-size:16px;\"><span style=\"font-size:16px;\"><span style=\"font-size:16px;\">200</span></span></span><span style=\"font-size:16px;\"><span style=\"font-size:16px;\">亩环保、绿色玫瑰花田、浓浓的爱！</span></span>\r\n	</p>\r\n	<p class=\"cjk\" style=\"text-indent:0.74cm;\">\r\n		<span style=\"font-size:16px;\"><span style=\"font-size:16px;\">家是根，家和万事兴，家和的最高境界是爱！</span></span>\r\n	</p>\r\n	<p class=\"cjk\" style=\"text-indent:0.74cm;\">\r\n		<span style=\"font-size:16px;\"><span style=\"font-size:16px;\">百事孝为先，孝不如顺，孝顺也是一种爱！</span></span>\r\n	</p>\r\n	<p class=\"cjk\" style=\"text-indent:0.74cm;\">\r\n		<span style=\"font-size:16px;\"><span style=\"font-size:16px;\">珍惜才会拥有，感恩才长久，感恩一切也是爱！</span></span>\r\n	</p>\r\n	<p class=\"cjk\" style=\"text-indent:0.74cm;\">\r\n		<span style=\"font-size:16px;\"><span style=\"font-size:16px;\">恭敬师长，恭敬长辈也是种爱！</span></span>\r\n	</p>\r\n	<p class=\"cjk\">\r\n		<b><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; 鉴于对爱的执念及回馈社会的理念，于2012年初成立了云南奇彩鲜花食品有限公司，注册资金壹佰万元，主营方向以玫瑰为主轴、生产及销售一系的鲜花食品，公司位于学府路690号昆明金鼎山科技园区内，公司所有员工都是用爱心、真诚心、、耐心、细心、责任心、做人、做事，贡献付出百分百服务大众，在短时间从原料着手，在安宁种植200多亩玫瑰基地，从源头把关，把环保、绿色、健康做为公司的发展方向。2015年被高新区评为“巾帼创新业示范基地”，并迅速构建了3条食品生产线，年产量300吨，主要产品以鲜花烘培类食品、月饼、鲜花云腿饼、鲜花饼、鲜花素饼、玫瑰花馅料、玫瑰花浆（酱）等一系列以鲜花为主的健康绿色食品，2015年年度被评为“云南省十佳月饼”。公司致力于开发无色素、香精的纯天然的鲜花食品，努力开拓花卉食用深加工行业，获准发明专利1项。汇聚善的力量、爱的文化、短短几年公司已向社会公益事业捐赠鲜花饼上万个，向多个寺院捐赠鲜花素饼共计五万余个。公司全体员工以诚信、感恩、协作的态度，全力以赴的把花之精华、爱之真诚融入奇彩鲜花食品，送给热爱自然和生活的人们。</span></b>\r\n	</p>\r\n	<p class=\"cjk\" style=\"text-indent:0.99cm;\">\r\n		<span style=\"font-size:16px;\"><span style=\"font-size:16px;\">花之懿、恋爱的感觉—满满的爱！</span></span>\r\n	</p>\r\n	<p class=\"cjk\" style=\"text-indent:0.99cm;\">\r\n		<span style=\"font-size:16px;\"><span style=\"font-size:16px;\">送出花之懿是满满的爱！</span></span>\r\n	</p>\r\n	<p class=\"cjk\" style=\"text-indent:0.99cm;\">\r\n		<span style=\"font-size:16px;\"><span style=\"font-size:16px;\">收到花之懿更是满满的爱！</span></span>\r\n	</p>\r\n<br />\r\n</b></span></span>\r\n</p>','2016-05-16 11:35:13','2016-05-16 11:35:13');
